import React, { Fragment, useState, useEffect } from 'react';
import Formulario from "./components/Formulario";
import Cita from "./components/Cita";



function App() {

  // Arreglo de citas
  const[citas, guardarCitas] = useState([]);

  //useEffect para realizar ciertas operaciones antes cambios del state
  useEffect( () => {
    console.log("Documento listo o algo paso con las citas")
  }, [citas])

  //Funcion que toma las citas actuales y agrega la nueva
  const crearCita = cita => {
    guardarCitas([
      ...citas,
      cita
    ]);
  }

  //Funcion que elimina citas por su id
    const eliminarCita = id => {
      const nuevasCitas = citas.filter(cita => cita.id !== id );
      guardarCitas(nuevasCitas);
    }
  //Mensaje condicional
  const titulo = citas.length === 0 ? "No hay citas" : "Adminitra tus citas"

  return (
    <Fragment>
      <h1>Administrador de Pacientes</h1>
      <div className="container">
        <div className="row">
          <div className="one-half column">
            <Formulario 
              crearCita={crearCita}
            />
          </div>
          <div className="one-half column">
            <h2>{titulo}</h2>
            {citas.map(cita => (
              <Cita 
              key={cita.id}
              cita={cita}
              eliminarCita={eliminarCita}               
              />
           ))}
          </div>

        </div>
      </div>

    </Fragment>
  );
}

export default App;
